#!/usr/bin/env node
var http_proxy = require("http-proxy");
var dnsserver = require("dnsserver");

var config_file = require("../lib/config_file");

// start dns
// start httpd router
// restart httpd when config changes.

var DNS_PORT = 3333;
var HTTP_PORT = 5999;

start_dns();
var httpd = start_httpd();

config_file.on('changed', function() {
  console.log("reloading proxy");
  httpd.close();
  console.log("closed");
  httpd = start_httpd();
  console.log("done");
});

function start_httpd()
{
  var routes = config_file.routes();
  var server = http_proxy.createServer({
    router: routes
  });

  server.on("listening", function() {
    console.log("httpd running");
  });
  server.listen(HTTP_PORT);
  return server;
}

function start_dns()
{
  var server = dnsserver.createServer();

  server.on("request", function(req, res) {
    var question = req.question;

    if (question.type == 1 && question.class == 1) {
      // IN A query
      res.addRR(question.name, 1, 1, 600, "127.0.0.1");
    } else {
      res.header.rcode = 3; // NXDOMAIN
    }

    res.send();
  });

  server.on("message", function(m) {
    console.log(m.toString('utf8'));
  });

  server.on("error", function(e) {
    console.log(e);
  });

  server.on("listening", function() {
    console.log("DNS is a go");
  });
  server.bind(DNS_PORT, "127.0.0.1");
}

var FILENAME = process.env.HOME + "/.local-tld.json";

var fs = require("fs");
var watch = require("watchfd").watch;
var EventEmitter = require("events").EventEmitter;
var get_local_ips = require("./get_local_ips");

var config_file = new EventEmitter();
var config = {};

function read()
{
  try {
    config = migrate_config(JSON.parse(fs.readFileSync(FILENAME)));
  } catch(e) {
    console.error("Parsing of config file %s failed:", FILENAME);
    console.error(e);
    config = {};
  }
}

function migrate_config(config)
{
  // Old config files expected alias lists to be an object
  // with the aliases as keys. We changed this to just be
  // an array of aliases, and are automagically migrating
  // everyone's config files for them.
  var changed = false;
  for (var port_m in config) {
    var aliases = config[port_m].aliases;
    if (typeof aliases === "object" && !Array.isArray(aliases)) {
      config[port_m].aliases = Object.keys(aliases);
      changed = true;
    }
  }
  if (changed) {
    try {
      fs.writeFileSync(FILENAME, JSON.stringify(config, null, 2));
    } catch(e) {
      console.error("Failed updating config file %s:", FILENAME);
      console.error(e);
    }
  }
  return config;
}

config_file.routes = function() {
  var routes = {};
  var local_ips = get_local_ips();
  for(var port_m in config) {
    var domain = config[port_m].name;
    routes["localhost"] = "127.0.0.1:80";
    routes[domain + ".dev"] = "127.0.0.1:" + port_m;
    local_ips.forEach(function(local_ip) {
      routes[domain + "." + local_ip + ".xip.io"] = "127.0.0.1:" + port_m;
    });
    if(config[port_m].aliases) {
      var aliases = config[port_m].aliases;
      for(var alias in aliases) {
        routes[".*" + aliases[alias] + "." + domain + ".dev"] = "127.0.0.1:" + port_m;
        local_ips.forEach(function(local_ip) {
            routes[".*" + aliases[alias] + "." + domain + "." + local_ip + ".xip.io"] = "127.0.0.1:" + port_m;
        });
      }
    }
  }
  console.log(routes);
  return routes;
};

watch(FILENAME, function(current, previous) {
  if(current.length == 0) {
    return;
  }
  read();
  config_file.emit('changed');
});

read();
module.exports = config_file;

var os = require("os");

function get_local_ips()
{
  var ifconfig = os.networkInterfaces();
  var local_ips = [];
  var if_name;

  for(if_name in ifconfig) {
    if(if_name.substr(0, 2) != "en") {
      continue; // for other unixes learn other interface names
    }
    var _if = ifconfig[if_name];
    _if.forEach(function(address_info) {
      if(address_info.internal) {
        return; // no loopback!
      }

      if(address_info.family == 'IPv6') {
        return; // no
      }

      local_ips.push(address_info.address);

    });
  }
  return local_ips;
}

module.exports = get_local_ips;
